    private void cbTodosItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbTodosItemStateChanged
        int estado = evt.getStateChange();        
               
        if(estado == ItemEvent.SELECTED){
            cbRojo.setSelected(true);
            rojo = true;
            
            cbVerde.setSelected(true);
            verde = true;
            
            cbAzul.setSelected(true);
            azul = true; 
        }        

        lienzo.setColores(rojo,verde,azul);
    }//GEN-LAST:event_cbTodosItemStateChanged

    private void cbRojoItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbRojoItemStateChanged
        int estado = evt.getStateChange();
        
        if(estado == ItemEvent.SELECTED){
            rojo = true;
        }
        
        if(estado == ItemEvent.DESELECTED){            
            if(verde == false && azul == false){
                cbRojo.setSelected(true);
            }else{
                rojo = false;
                cbTodos.setSelected(false);  
            }            
        }
        
        if(rojo == true && azul == true && verde == true){
            cbTodos.setSelected(true);
        }
        
        
        
        lienzo.setColores(rojo,verde,azul);
    }//GEN-LAST:event_cbRojoItemStateChanged

    private void cbVerdeItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbVerdeItemStateChanged
        int estado = evt.getStateChange();
        
        if(estado == ItemEvent.SELECTED){
            verde = true;
        }
        
        if(estado == ItemEvent.DESELECTED){            
            if(rojo == false && azul == false){
                cbVerde.setSelected(true);
            }else{
                verde = false;
                cbTodos.setSelected(false); 
            }         
        }
        
        if(rojo == true && azul == true && verde == true){
            cbTodos.setSelected(true);
        }
        
        lienzo.setColores(rojo,verde,azul);
    }//GEN-LAST:event_cbVerdeItemStateChanged

    private void cbAzulItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbAzulItemStateChanged
        int estado = evt.getStateChange();
        
        if(estado == ItemEvent.SELECTED){
            azul = true;
        }
        
        if(estado == ItemEvent.DESELECTED){
            if(rojo == false && verde == false){
                cbAzul.setSelected(true);
            }else{
                azul = false;
                cbTodos.setSelected(false);
            }            
        }
        
        if(rojo == true && azul == true && verde == true){
            cbTodos.setSelected(true);
        }
        
        lienzo.setColores(rojo,verde,azul);
    }//GEN-LAST:event_cbAzulItemStateChanged

